from selenium import webdriver
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import NoSuchWindowException, WebDriverException, TimeoutException
import time
import logging
import atexit

logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')

def initialize_driver():
    chrome_options = Options()
    chrome_options.add_argument("--start-maximized")
    chrome_options.add_argument("--disable-extensions")
    chrome_options.add_argument("--disable-popup-blocking")
    chrome_options.add_experimental_option("excludeSwitches", ["enable-automation"])
    
    service = Service("C:/Program Files/chromedriver/chromedriver.exe")  # Update this path as needed
    driver = webdriver.Chrome(service=service, options=chrome_options)
    return driver

def inject_listeners(driver):
    script = """
    var hoverTimeout;
    document.addEventListener('click', function(event) {
        var element = event.target;
        window.clickedElementHtml = element.outerHTML;
        console.log('Element clicked: ' + window.clickedElementHtml);
    }, true);
    document.addEventListener('mouseover', function(event) {
        var element = event.target;
        hoverTimeout = setTimeout(function() {
            window.hoveredElementHtml = element.outerHTML;
            console.log('Element hovered for 5 seconds: ' + window.hoveredElementHtml);
        }, 5000);
    }, true);
    document.addEventListener('mouseout', function(event) {
        clearTimeout(hoverTimeout);
    }, true);
    """
    driver.execute_script(script)

def run_test_recorder(max_recording_time=300, min_interactions=20):
    driver = None
    page_clicks_map = {}
    page_hovers_map = {}
    start_time = time.time()

    def cleanup():
        logging.info("Shutdown hook triggered. Printing recorded elements...")
        print_recorded_elements(page_clicks_map, page_hovers_map)
        if driver:
            driver.quit()

    atexit.register(cleanup)

    try:
        driver = initialize_driver()
        driver.get("https://opensource-demo.orangehrmlive.com/")
        
        inject_listeners(driver)
        
        current_url = driver.current_url
        page_clicks_map[current_url] = []
        page_hovers_map[current_url] = []
        
        wait = WebDriverWait(driver, 10)
        
        while True:
            if time.time() - start_time > max_recording_time:
                logging.info("Maximum recording time reached. Stopping recording.")
                break

            try:
                if current_url != driver.current_url:
                    current_url = driver.current_url
                    wait.until(EC.presence_of_element_located((By.TAG_NAME, "body")))
                    inject_listeners(driver)
                    page_clicks_map.setdefault(current_url, [])
                    page_hovers_map.setdefault(current_url, [])
                
                clicked_element_html = driver.execute_script("return window.clickedElementHtml;")
                if clicked_element_html and clicked_element_html not in page_clicks_map[current_url]:
                    page_clicks_map[current_url].append(clicked_element_html)
                    logging.info(f"Clicked Element: {clicked_element_html[:100]}...")
                
                hovered_element_html = driver.execute_script("return window.hoveredElementHtml;")
                if hovered_element_html and hovered_element_html not in page_hovers_map[current_url]:
                    page_hovers_map[current_url].append(hovered_element_html)
                    logging.info(f"Hovered Element: {hovered_element_html[:100]}...")
                
                total_interactions = sum(len(clicks) for clicks in page_clicks_map.values()) + \
                                     sum(len(hovers) for hovers in page_hovers_map.values())
                
                if total_interactions >= min_interactions:
                    logging.info(f"Minimum interactions ({min_interactions}) reached. Stopping recording.")
                    break

                time.sleep(1)

            except (NoSuchWindowException, WebDriverException) as e:
                logging.error(f"Browser window closed or WebDriver exception occurred: {str(e)}")
                break
            except TimeoutException:
                logging.warning("Timeout waiting for page to load. Continuing...")
            except Exception as e:
                logging.error(f"An unexpected error occurred: {str(e)}")
                break

    except Exception as e:
        logging.error(f"An unexpected error occurred: {str(e)}")

    finally:
        if driver:
            driver.quit()

    return page_clicks_map, page_hovers_map

if __name__ == "__main__":
    page_clicks, page_hovers = run_test_recorder()
    print_recorded_elements(page_clicks, page_hovers)
